
public class FactorialNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5, Fact = 1,i;
		
        for(i=1;i<=n;i++) {
        	Fact=Fact*i;
        }
        System.out.println("Factorial of the Given Number "+n+" is "+Fact);
		
		
	}

}
